import {
    MenuFoldOutlined,
    MenuUnfoldOutlined,
} from '@ant-design/icons';
import { Layout, Dropdown, Menu } from 'antd';
import React, { useState } from 'react';
const { Header } = Layout;

export default function TopHeader() {

    const [collapsed, setCollapsed] = useState(false);

    const changeCollapsed = () => {
        setCollapsed(!collapsed);
    }

    const menu = (
        <Menu>
            <Menu.Item>111</Menu.Item>
            <Menu.Item>222</Menu.Item>
            <Menu.Item>333</Menu.Item>
            <Menu.Item>444</Menu.Item>
            </Menu>
    );

    return (
        <Header
            className="site-layout-background"
            style={{
                padding: 0,
            }}
        >
            {
                collapsed ?
                    <MenuUnfoldOutlined onClick={changeCollapsed} /> : <MenuFoldOutlined onClick={changeCollapsed} />
            }

            <div style={{ float: 'right' }}>
                <span>Welcome xxx back!</span>
                <Dropdown overlay={menu}>
                    <span>hover me</span>
                </Dropdown>
            </div>
        </Header>
    )
}
