import {
  Button,
  Table,
  Modal,
  Switch,
} from 'antd'
import {
  DeleteOutlined,
  EditOutlined,
} from '@ant-design/icons'
import axios from 'axios'
import React, { useEffect, useRef, useState } from 'react'
import { ExclamationCircleOutlined } from '@ant-design/icons'
import UserForm from '../../../components/user-manage/UserForm'

const { confirm } = Modal;

export default function UserList() {

  const [dataSource, setDataSource] = useState([])
  const [isAddVisible, setIsAddVisible] = useState(false)
  const [roleList, setRoleList] = useState([])
  const [regionList, setRegionList] = useState([])
  const addForm = useRef(null)

  useEffect(() => {
    axios.get("http://localhost:5000/users?_expand=role")
      .then(res => {
        setDataSource(res.data)
      })
  }, [])

  useEffect(() => {
    axios.get("http://localhost:5000/regions")
      .then(res => {
        setRegionList(res.data)
      })
  }, [])

  useEffect(() => {
    axios.get("http://localhost:5000/roles")
      .then(res => {
        setRoleList(res.data)
      })
  }, [])

  const columns = [
    {
      title: 'Region Area',
      dataIndex: 'region',
      render: (region) => {
        return <b>{region === "" ? "Global" : region}</b>
      }
    },
    {
      title: 'User Title',
      dataIndex: 'role',
      render: (role) => {
        return role?.roleName
      }
    },
    {
      title: 'User Name',
      dataIndex: 'username',
    },
    {
      title: 'User State',
      dataIndex: 'roleState',
      render: (roleState, item) => {
        return <Switch checked={roleState} disabled={item.default} 
          onChange={() => handleChange(item)}
        ></Switch>
      }
    },
    {
      title: 'Handle',
      render: (item) => {
        return (
          <div>
            <Button
              type="danger"
              shape="circle"
              icon={<DeleteOutlined />}
              disabled={item.default}
              onClick={() => showConfirm(item)}
            />
            <Button
              type="primary"
              shape="circle"
              disabled={item.default}
              icon={<EditOutlined />}
            />
          </div>
        )
      }
    },
  ];

  const handleChange = (item) => {
    console.log(item)
    item.roleState = !item.roleState
    setDataSource([...dataSource])
  }

  const showConfirm = (item) => {
    confirm({
      title: 'Do you Want to delete these items?',
      icon: <ExclamationCircleOutlined />,
      //content: 'Some descriptions',
      onOk() {
        //console.log('OK');
        deleteMethod(item);
      },
      onCancel() {
        //console.log('Cancel');
      },
    });
  };

  const deleteMethod = (item) => {
    //console.log(item)
    setDataSource(dataSource.filter(data => data.id === item.id))
    axios.delete(`http://localhost:5000/users/${item.id}`)
      .then(res => {

      })
  }

  const addFormOk = () => {
    //console.log('added', addForm)
    // get value from form and here to fetch the from values
    addForm.current.validateFields().then(value => {
      //console.log(value)
      setIsAddVisible(false)
      //reset form after submitting
      addForm.current.resetFields()
      // post to backend, and generate ID, then set the datasource
      // this way is easier to CRUD
      axios.post("http://localhost:5000/users", {
        ...value,
        "roleState":true,
        "default": false,
      }).then( res => {
        //console.log(res.data)
        setDataSource([...dataSource, {
          ...res.data,
          role:roleList.filter(item => item.id === value.roleId)[0]
        }])
      })
    }).catch(error => {
      console.log(error)
    })
  }

  return (
    <div>
      <Button
        type='primary'
        onClick={() => setIsAddVisible(true)}
      >
        ADD USER
      </Button>
      <Table
        dataSource={dataSource}
        columns={columns}
        pagination={{
          pageSize: 5
        }}
        rowKey={item => item.id}
      />
      <Modal
        open={isAddVisible}
        title="Add A User"
        okText="Confirm"
        cancelText="Cancel"
        onCancel={() => setIsAddVisible(false)}
        onOk={() => { addFormOk() }}
      >
        <UserForm
          regionList={regionList}
          roleList={roleList}
          ref={addForm}
        />
      </Modal>
    </div>
  )
}
