import {
    Form,
    Input,
    Select
} from 'antd'
import axios from 'axios'
import React, { useEffect, useState } from 'react'

const { Option } = Select;


export default function UserForm() {
    const [roleList, setRoleList] = useState([])
    const [regionList, setRegionList] = useState([])

    useEffect(() => {
        axios.get("http://localhost:5000/regions")
            .then(res => {
                setRegionList(res.data)
            })
    }, [])

    useEffect(() => {
        axios.get("http://localhost:5000/roles")
            .then(res => {
                setRoleList(res.data)
            })
    }, [])
    return (
        <div>
            <Form
                layout="vertical"
            >
                <Form.Item
                    name="username"
                    label="Username"
                    rules={[
                        {
                            required: true,
                            message: 'Please input the title of user!',
                        },
                    ]}
                >
                    <Input />
                </Form.Item>
                <Form.Item
                    name="password"
                    label="Password"
                    rules={[
                        {
                            required: true,
                            message: 'Please input the password of user!',
                        },
                    ]}
                >
                    <Input />
                </Form.Item>
                <Form.Item
                    name="region"
                    label="Region Area"
                    rules={[
                        {
                            required: true,
                            message: 'Please input the region of user!',
                        },
                    ]}
                >
                    <Select>
                        {regionList.map(item => {
                            return <Option value={item.value} key={item.id} >{item.title}</Option>
                        })}
                    </Select>
                </Form.Item>
                <Form.Item
                    name="roleId"
                    label="User Title"
                    rules={[
                        {
                            required: true,
                            message: 'Please input the UserTitle of user!',
                        },
                    ]}
                >
                    <Select>
                        {roleList.map(item => <Option key={item.id} value={item.id}>{item.roleName}</Option>)}
                    </Select>
                </Form.Item>
            </Form>
        </div>
    )
}
